// Only run on the first incoming item
if ($itemIndex !== 0) {
  return [];
}

// Step 1: Collect all Post IDs from Google Sheet rows
const postIds = items.map(item => item.json['Post ID']);

// Step 2: Get the Limit node data (assuming only 1 item there)
const limitItem = $items('Limit')[0].json;
const limitGuid = limitItem.guid;

// Step 3: Check if the guid already exists in the sheet
const guidExists = postIds.includes(limitGuid);

// Step 4: Return only once
if (guidExists) {
    // If found â†’ return nothing
    return [];
} else {
    // If not found â†’ return Limit node data once
    return [
        {
            json: {
                title: limitItem.title,
                content: limitItem.content,
                id: limitItem.guid
            }
        }
    ];
}
